import React, { useState, useEffect, useRef } from 'react';
import { FiSend, FiUser, FiMessageSquare } from 'react-icons/fi';

const ChatInterface = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim() === '') return;

    const newMsg = {
      id: messages.length + 1,
      sender: 'You',
      content: newMessage,
      timestamp: new Date().toISOString(),
    };

    setMessages([...messages, newMsg]);
    setNewMessage('');
  };

  return (
    <div className="flex h-screen bg-[#0F172A] text-[#E5E7EB]">
      <div className="w-64 bg-[#1E3A8A] p-4 overflow-y-auto">
        <h2 className="text-xl font-bold mb-4 text-[#FACC15]">Users</h2>
        <ul className="space-y-2">
          {users.map((user) => (
            <li
              key={user.id}
              className={`flex items-center p-2 rounded-lg cursor-pointer transition-colors duration-200 ${
                selectedUser?.id === user.id ? 'bg-[#2563EB]' : 'hover:bg-[#312E81]'
              }`}
              onClick={() => setSelectedUser(user)}
            >
              <FiUser className="mr-2 text-[#FACC15]" />
              <span>{user.name}</span>
              <span className={`ml-auto w-2 h-2 rounded-full ${user.status === 'online' ? 'bg-green-500' : 'bg-gray-500'}`}></span>
            </li>
          ))}
        </ul>
      </div>

      <div className="flex-1 flex flex-col">
        {selectedUser ? (
          <>
            {/* Chat Header */}
            <div className="bg-[#1E3A8A] p-4 flex items-center">
              <FiUser className="mr-2 text-[#FACC15]" />
              <span className="font-bold">{selectedUser.name}</span>
              <span className={`ml-2 w-2 h-2 rounded-full ${selectedUser.status === 'online' ? 'bg-green-500' : 'bg-gray-500'}`}></span>
            </div>

            {/* Chat Messages */}
            <div className="flex-1 overflow-y-auto p-4 space-y-4">
              {messages.map((message) => (
                <div
                  key={message.id}
                  className={`flex ${message.sender === 'You' ? 'justify-end' : 'justify-start'}`}
                >
                  <div
                    className={`max-w-xs p-3 rounded-lg ${
                      message.sender === 'You' ? 'bg-[#2563EB]' : 'bg-[#312E81]'
                    }`}
                  >
                    <p className="font-bold">{message.sender}</p>
                    <p>{message.content}</p>
                    <p className="text-xs text-[#A78BFA] mt-1">
                      {new Date(message.timestamp).toLocaleTimeString()}
                    </p>
                  </div>
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>

            {/* Message Input */}
            <form onSubmit={handleSendMessage} className="bg-[#1E3A8A] p-4">
              <div className="flex items-center">
                <input
                  type="text"
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  placeholder="Type a message..."
                  className="flex-1 bg-[#312E81] text-[#E5E7EB] placeholder-[#A78BFA] rounded-l-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-[#2563EB]"
                />
                <button
                  type="submit"
                  className="bg-[#2563EB] text-[#E5E7EB] rounded-r-lg px-4 py-2 hover:bg-[#1E40AF] transition-colors duration-300"
                >
                  <FiSend />
                </button>
              </div>
            </form>
          </>
        ) : (
          // No user selected
          <div className="flex-1 flex items-center justify-center">
            <div className="text-center">
              <FiMessageSquare className="mx-auto text-6xl text-[#A78BFA] mb-4" />
              <p className="text-xl">Select a user to start chatting</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatInterface;
