import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { jwtDecode } from 'jwt-decode';
import { FiSend, FiUser, FiMessageSquare, FiCircle, FiSearch, FiX } from 'react-icons/fi';

const getCurrentUserId = () => {
  try {
    const token = localStorage.getItem('token');
    if (!token) return null;
    const decodedToken = jwtDecode(token);
    return decodedToken.user.id;
  } catch (error) {
    console.error('Error decoding token:', error);
    return null;
  }
};

const ChatInterface = () => {
  const [conversations, setConversations] = useState([]);
  const [messages, setMessages] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [newMessage, setNewMessage] = useState('');
  const [socket, setSocket] = useState(null);
  const messagesEndRef = useRef(null);
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [users, setUsers] = useState([]);
  const [tempConversationId, setTempConversationId] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const newSocket = io('http://localhost:5000', {
      auth: { token }
    });
    setSocket(newSocket);

    const fetchConversations = async () => {
      try {
        const response = await fetch('/api/chat/conversations', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        const data = await response.json();
        setConversations(data);
      } catch (error) {
        console.error('Fetch conversations error:', error);
      }
    };
    fetchConversations();

    newSocket.on('new_message', (message) => {
      if (selectedConversation && message.conversationId === selectedConversation.conversationId) {
        setMessages((prev) => [...prev, message]);
      }
    });

    newSocket.on('message_sent', (message) => {
      if (tempConversationId) {
        setTempConversationId(null);
      }
    });

    newSocket.on('user_status_change', (statusUpdate) => {
      setConversations((prev) =>
        prev.map((conv) =>
          conv.user._id === statusUpdate.userId
            ? { ...conv, user: { ...conv.user, isOnline: statusUpdate.status === 'online' } }
            : conv
        )
      );
    });

    return () => {
      newSocket.disconnect();
    };
  }, []);

  useEffect(() => {
    if (!selectedConversation) return;

    const fetchMessages = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(
          `/api/chat/messages/${selectedConversation.conversationId}`,
          {
            method: 'GET',
            headers: {
              'Authorization': `Bearer ${token}`
            }
          }
        );
        const data = await response.json();
        setMessages(data);
      } catch (error) {
        console.error('Fetch messages error:', error);
      }
    };
    fetchMessages();
  }, [selectedConversation]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = async () => {
    if (!newMessage.trim()) return;

    if (selectedConversation.conversationId === null) {
      // Temporary conversation: Call sendMessage API
      try {
        const { data } = await axios.post('/api/chat/send-message', {
          recipientId: selectedConversation.user._id,
          content: newMessage
        });

        const newConversation = {
          conversationId: data.data.conversationId,
          user: selectedConversation.user,
          lastMessage: data.data.message
        };

        setConversations([newConversation, ...conversations]);
        setSelectedConversation(newConversation);
        setNewMessage('');
      } catch (error) {
        console.error('Error sending message:', error);
      }
    } else {
      // Existing conversation: Use socket.emit
      const messageData = {
        conversationId: selectedConversation.conversationId,
        senderId: userId,
        content: newMessage,
        timestamp: new Date()
      };

      socket.emit('send_message', messageData);

      // Update conversation locally
      const updatedConversations = conversations.map(conv => {
        if (conv.conversationId === selectedConversation.conversationId) {
          return {
            ...conv,
            lastMessage: messageData
          };
        }
        return conv;
      });

      setConversations(updatedConversations);
      setMessage('');
    }
  };

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/auth/getusers', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        if (!response.ok) {
          throw new Error(`Failed to fetch workers: ${response.status}`);
        }
        const usersData = await response.json();
        setUsers(Array.isArray(usersData) ? usersData : [usersData]);
      } catch (error) {
        console.error('Error fetching workers:', error);
      }
    };

    fetchUsers();
  }, []);

  useEffect(() => {
    const filtered = users.filter(user =>
      user.username.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setTimeout(() => {
      setSearchResults(filtered);
    }, 100);
  }, [searchQuery, users]);

  const handleStartConversation = async (user) => {
    const existingConversation = conversations.find(
      conv => conv.user._id === user._id
    );

    if (existingConversation) {
      setSelectedConversation(existingConversation);
    } else {
      const tempConversation = {
        conversationId: null,
        user
      };
      setSelectedConversation(tempConversation);
    }
  };
  const handleBackFromTemporaryChat = async () => {
    // If there's no messages, delete the temporary conversation
    if (selectedConversation && !selectedConversation.conversationId) {
      try {
        await fetch(`/api/chat/delete-temporary-conversation`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          },
          body: JSON.stringify({ 
            selectedUserId: selectedConversation.user._id 
          })
        });
      } catch (error) {
        console.error('Error deleting temporary conversation:', error);
      }
    }

    // Reset selected conversation
    setSelectedConversation(null);
  };

  return (
    <div className="flex h-screen bg-[#0F172A] text-[#E5E7EB]">
      {/* Top section */}
      <div className="absolute top-0 left-0 right-0 bg-[#1E3A8A] p-2 flex items-center justify-between z-10">
        <h1 className="text-xl font-bold text-[#FACC15]">FuturChat</h1>
        <div className="flex items-center">
          {selectedConversation && !selectedConversation.conversationId && (
            <button 
              onClick={handleBackFromTemporaryChat}
              className="mr-2 bg-[#2563EB] text-[#E5E7EB] rounded-full p-2 hover:bg-[#1E40AF] transition-colors duration-300"
            >
              <FiX />
            </button>
          )}
          <button
            onClick={() => setIsSearchOpen(!isSearchOpen)}
            className="bg-[#2563EB] text-[#E5E7EB] rounded-full p-2 hover:bg-[#1E40AF] transition-colors duration-300"
          >
            <FiSearch />
          </button>
        </div>
      </div>

      {/* Main content */}
      <div className="flex w-full pt-12 transition-all duration-300 ease-in-out">
        {/* Conversations Sidebar */}
        <div className={`w-64 bg-[#1E3A8A] p-4 overflow-y-auto h-[calc(100vh-3rem)] transition-all duration-300 ease-in-out ${isSearchOpen ? 'w-48' : 'w-64'}`}>
          <h2 className="text-xl font-bold mb-4 text-[#FACC15]">Chats</h2>
          <ul className="space-y-2">
            {conversations.map((conv) => (
              <li
                key={conv.conversationId}
                className={`flex items-center p-2 rounded-lg cursor-pointer transition-colors duration-200 ${
                  selectedConversation?.conversationId === conv.conversationId ? 'bg-[#2563EB]' : 'hover:bg-[#312E81]'
                }`}
                onClick={() => setSelectedConversation(conv)}
              >
                <FiUser className="mr-2 text-[#FACC15]" />
                <span className="truncate">{conv.user.name}</span>
                <FiCircle
                  className={`ml-auto w-2 h-2 ${
                    conv.user.isOnline ? 'text-green-500' : 'text-gray-500'
                  }`}
                />
              </li>
            ))}
          </ul>
        </div>

        {/* Chat Area */}
        <div className={`flex-1 flex flex-col h-[calc(100vh-3rem)] transition-all duration-300 ease-in-out ${isSearchOpen ? 'mr-64' : ''}`}>
          {selectedConversation ? (
            <>
              {/* Chat Header */}
              <div className="bg-[#1E3A8A] p-4 flex items-center">
                <FiUser className="mr-2 text-[#FACC15]" />
                <span className="font-bold">{selectedConversation.user.name}</span>
                <FiCircle
                  className={`ml-2 w-2 h-2 ${
                    selectedConversation.user.isOnline ? 'text-green-500' : 'text-gray-500'
                  }`}
                />
              </div>

              {/* Chat Messages */}
              <div className="flex-1 overflow-y-auto p-4 space-y-4">
                {messages.map((message) => (
                  <div
                    key={message._id}
                    className={`flex ${message.senderId === getCurrentUserId() ? 'justify-end' : 'justify-start'}`}
                  >
                    <div
                      className={`max-w-xs p-3 rounded-lg ${
                        message.senderId === getCurrentUserId() ? 'bg-[#2563EB]' : 'bg-[#312E81]'
                      }`}
                    >
                      <p>{message.content}</p>
                      <p className="text-xs text-[#A78BFA] mt-1">
                        {new Date(message.timestamp).toLocaleTimeString()}
                      </p>
                    </div>
                  </div>
                ))}
                <div ref={messagesEndRef} />
              </div>

              {/* Message Input */}
              <form onSubmit={handleSendMessage} className="bg-[#1E3A8A] p-4">
                <div className="flex items-center">
                  <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    placeholder="Type a message..."
                    className="flex-1 bg-[#312E81] text-[#E5E7EB] placeholder-[#A78BFA] rounded-l-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-[#2563EB]"
                  />
                  <button
                    type="submit"
                    className="bg-[#2563EB] text-[#E5E7EB] rounded-r-lg px-4 py-2 hover:bg-[#1E40AF] transition-colors duration-300"
                  >
                    <FiSend />
                  </button>
                </div>
              </form>
            </>
          ) : (
            // No conversation selected
            <div className="flex-1 flex items-center justify-center">
              <div className="text-center">
                <FiMessageSquare className="mx-auto text-6xl text-[#A78BFA] mb-4" />
                <p className="text-xl">Select a conversation to start chatting</p>
              </div>
            </div>
          )}
        </div>

        {/* Search Capsule */}
        <div
          className={`fixed right-0 top-12 bottom-0 bg-[#1E3A8A] w-64 transition-all duration-300 ease-in-out transform ${
            isSearchOpen ? 'translate-x-0' : 'translate-x-full'
          }`}
        >
          <div className="p-4">
            <div className="flex items-center mb-4">
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="Search users..."
                className="flex-1 bg-[#312E81] text-[#E5E7EB] placeholder-[#A78BFA] rounded-l-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-[#2563EB]"
              />
            </div>
            <div className="overflow-y-auto h-[calc(100vh-8rem)]">
              {searchResults.map((user) => (
                <div
                  key={user._id}
                  className="flex items-center p-2 rounded-lg cursor-pointer hover:bg-[#312E81] transition-colors duration-200"
                  onClick={() => handleStartConversation(user)}
                >
                  <FiUser className="mr-2 text-[#FACC15]" />
                  <span>{user.name}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
