import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { jwtDecode } from 'jwt-decode';
import { FiSend, FiUser, FiMessageSquare, FiCircle, FiSearch, FiX } from 'react-icons/fi';

// Function to get the current user's ID from the JWT token
const getCurrentUserId = () => {
  try {
    const token = localStorage.getItem('token');
    if (!token) return null;
    const decodedToken = jwtDecode(token);
    return decodedToken.user.id;
  } catch (error) {
    console.error('Error decoding token:', error);
    return null;
  }
};

const ChatInterface = () => {
  const [conversations, setConversations] = useState([]);
  const [messages, setMessages] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [newMessage, setNewMessage] = useState('');
  const [socket, setSocket] = useState(null);
  const messagesEndRef = useRef(null);
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const newSocket = io('http://localhost:5000', {
      auth: { token }
    });
    setSocket(newSocket);

    const fetchConversations = async () => {
      try {
        const response = await fetch('/api/chat/conversations', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        const data = await response.json();
        setConversations(data);
      } catch (error) {
        console.error('Fetch conversations error:', error);
      }
    };
    fetchConversations();

    newSocket.on('new_message', (message) => {
      if (selectedConversation && message.conversationId === selectedConversation.conversationId) {
        setMessages((prev) => [...prev, message]);
      }
    });

    newSocket.on('user_status_change', (statusUpdate) => {
      setConversations((prev) =>
        prev.map((conv) =>
          conv.user._id === statusUpdate.userId
            ? { ...conv, user: { ...conv.user, isOnline: statusUpdate.status === 'online' } }
            : conv
        )
      );
    });

    return () => {
      newSocket.disconnect();
    };
  }, []);

  useEffect(() => {
    if (!selectedConversation) return;

    const fetchMessages = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(
          `/api/chat/messages/${selectedConversation.conversationId}`,
          {
            method: 'GET',
            headers: {
              'Authorization': `Bearer ${token}`
            }
          }
        );
        const data = await response.json();
        setMessages(data);
      } catch (error) {
        console.error('Fetch messages error:', error);
      }
    };
    fetchMessages();
  }, [selectedConversation]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !selectedConversation || !socket) return;

    const messageData = {
      conversationId: selectedConversation.conversationId,
      senderId: getCurrentUserId(),
      receiverId: selectedConversation.user._id,
      content: newMessage
    };

    socket.emit('send_message', messageData);
    setNewMessage('');
  };

  const handleSearch = async () => {
    if (!searchQuery.trim()) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`/api/users/search?query=${searchQuery}`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      setSearchResults(data);
    } catch (error) {
      console.error('Search error:', error);
    }
  };

  useEffect(() => {
    if (isSearchOpen && searchQuery.trim()) {
      handleSearch();
    }
  }, [searchQuery, isSearchOpen]);

  return (
    <div className="flex h-screen bg-[#0F172A] text-[#E5E7EB]">
      {/* Top section */}
      <div className="absolute top-0 left-0 right-0 bg-[#1E3A8A] p-2 flex items-center justify-between z-10">
        <h1 className="text-xl font-bold text-[#FACC15]">FuturChat</h1>
        <button
  onClick={() => setIsSearchOpen((prev) => !prev)} // Toggle the state
  className="bg-[#2563EB] text-[#E5E7EB] rounded-lg px-4 py-2 hover:bg-[#1E40AF] transition-colors duration-300 flex items-center"
>
  <FiSearch className="mr-2" />
  <span>{isSearchOpen ? 'Close Search' : 'Search New Users'}</span> {/* Update label dynamically */}
</button>

      </div>

      {/* Main content */}
      <div className="flex w-full pt-12">
        {/* Conversations Sidebar */}
        <div className="w-64 bg-[#1E3A8A] p-4 overflow-y-auto h-[calc(100vh-3rem)]">
          <h2 className="text-xl font-bold mb-4 text-[#FACC15]">Chats</h2>
          <ul className="space-y-2">
            {conversations.map((conv) => (
              <li
                key={conv.conversationId}
                className={`flex items-center p-2 rounded-lg cursor-pointer transition-colors duration-200 ${
                  selectedConversation?.conversationId === conv.conversationId ? 'bg-[#2563EB]' : 'hover:bg-[#312E81]'
                }`}
                onClick={() => setSelectedConversation(conv)}
              >
                <FiUser className="mr-2 text-[#FACC15]" />
                <span className="truncate">{conv.user.name}</span>
                <FiCircle
                  className={`ml-auto w-2 h-2 ${
                    conv.user.isOnline ? 'text-green-500' : 'text-gray-500'
                  }`}
                />
              </li>
            ))}
          </ul>
        </div>

        {/* Chat Area */}
        <div className="flex-1 flex flex-col h-[calc(100vh-3rem)] relative">
          {selectedConversation ? (
            <>
              {/* Chat Header */}
              <div className="bg-[#1E3A8A] p-4 flex items-center">
                <FiUser className="mr-2 text-[#FACC15]" />
                <span className="font-bold">{selectedConversation.user.name}</span>
                <FiCircle
                  className={`ml-2 w-2 h-2 ${
                    selectedConversation.user.isOnline ? 'text-green-500' : 'text-gray-500'
                  }`}
                />
              </div>

              {/* Chat Messages and Search Capsule */}
              <div className="flex-1 overflow-hidden relative">
                {/* Search Capsule */}
                <div
                  className={`absolute top-0 right-0 bottom-0 bg-[#1E3A8A] rounded-l-full transition-all duration-300 ease-in-out overflow-hidden ${
                    isSearchOpen ? 'w-64' : 'w-0'
                  }`}
                >
                  <div className="p-4 h-full flex flex-col">
                    <div className="mb-4">
                      <input
                        type="text"
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        placeholder="Search users..."
                        className="w-full bg-[#312E81] text-[#E5E7EB] placeholder-[#A78BFA] rounded-full px-4 py-2 focus:outline-none focus:ring-2 focus:ring-[#2563EB]"
                      />
                    </div>
                    <div className="flex-1 overflow-y-auto">
                      {searchResults.map((user) => (
                        <div
                          key={user._id}
                          className="flex items-center p-2 rounded-lg cursor-pointer hover:bg-[#312E81] transition-colors duration-200"
                          onClick={() => {
                            // Handle starting a new conversation or selecting an existing one
                            setIsSearchOpen(false);
                          }}
                        >
                          <FiUser className="mr-2 text-[#FACC15]" />
                          <span>{user.name}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>

                {/* Messages */}
                <div
                  className={`h-full overflow-y-auto p-4 space-y-4 transition-all duration-300 ease-in-out ${
                    isSearchOpen ? 'mr-64' : ''
                  }`}
                >
                  {messages.map((message) => (
                    <div
                      key={message._id}
                      className={`flex ${message.senderId._id === getCurrentUserId() ? 'justify-end' : 'justify-start'}`}
                    >
                      <div
                        className={`max-w-xs p-3 rounded-lg ${
                          message.senderId._id === getCurrentUserId() ? 'bg-[#2563EB]' : 'bg-[#312E81]'
                        }`}
                      >
                        <p>{message.content}</p>
                        <p className="text-xs text-[#A78BFA] mt-1">
                          {new Date(message.timestamp).toLocaleTimeString()}
                        </p>
                      </div>
                    </div>
                  ))}
                  <div ref={messagesEndRef} />
                </div>
              </div>

              {/* Message Input */}
              <form onSubmit={handleSendMessage} className="bg-[#1E3A8A] p-4">
                <div className="flex items-center">
                  <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    placeholder="Type a message..."
                    className="flex-1 bg-[#312E81] text-[#E5E7EB] placeholder-[#A78BFA] rounded-l-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-[#2563EB]"
                  />
                  <button
                    type="submit"
                    className="bg-[#2563EB] text-[#E5E7EB] rounded-r-lg px-4 py-2 hover:bg-[#1E40AF] transition-colors duration-300"
                  >
                    <FiSend />
                  </button>
                </div>
              </form>
            </>
          ) : (
            // No conversation selected
            <div className="flex-1 flex items-center justify-center">
              <div className="text-center">
                <FiMessageSquare className="mx-auto text-6xl text-[#A78BFA] mb-4" />
                <p className="text-xl">Select a conversation to start chatting</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
