import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { jwtDecode } from 'jwt-decode';
import { FiSend, FiUser, FiMessageSquare, FiCircle } from 'react-icons/fi';

// Function to get the current user's ID from the JWT token
const getCurrentUserId = () => {
  try {
    const token = localStorage.getItem('token'); // Retrieve token from localStorage
    if (!token) return null;

    const decodedToken = jwtDecode(token); // Decode the token
    return decodedToken.user.id; // Adjust this based on your payload structure
  } catch (error) {
    console.error('Error decoding token:', error);
    return null;
  }
};

const ChatInterface = () => {
  const [conversations, setConversations] = useState([]);
  const [messages, setMessages] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [newMessage, setNewMessage] = useState('');
  const [socket, setSocket] = useState(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    // Establish socket connection
    const token = localStorage.getItem('token');
    const newSocket = io('http://localhost:5000', {
      auth: { token }
    });
    setSocket(newSocket);

    // Fetch conversations
    const fetchConversations = async () => {
      try {
        const response = await fetch('/api/chat/conversations', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        const data = await response.json();
        setConversations(data);
      } catch (error) {
        console.error('Fetch conversations error:', error);
      }
    };
    fetchConversations();

    // Socket event listeners
    newSocket.on('new_message', (message) => {
      if (
        selectedConversation &&
        message.conversationId === selectedConversation.conversationId
      ) {
        setMessages((prev) => [...prev, message]);
      }
    });

    newSocket.on('user_status_change', (statusUpdate) => {
      setConversations((prev) =>
        prev.map((conv) =>
          conv.user._id === statusUpdate.userId
            ? { ...conv, user: { ...conv.user, isOnline: statusUpdate.status === 'online' } }
            : conv
        )
      );
    });

    return () => {
      newSocket.disconnect();
    };
  }, []);

  useEffect(() => {
    if (!selectedConversation) return;

    const fetchMessages = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(
          `/api/chat/messages/${selectedConversation.conversationId}`,
          {
            method: 'GET',
            headers: {
              'Authorization': `Bearer ${token}`
            }
          }
        );
        const data = await response.json();
        setMessages(data);
      } catch (error) {
        console.error('Fetch messages error:', error);
      }
    };
    fetchMessages();
  }, [selectedConversation]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !selectedConversation || !socket) return;

    const messageData = {
      conversationId: selectedConversation.conversationId,
      senderId: getCurrentUserId(), // Get the current user's ID
      receiverId: selectedConversation.user._id,
      content: newMessage
    };

    socket.emit('send_message', messageData);
    setNewMessage('');
  };

  return (
    <div className="flex h-screen bg-gray-900 text-white">
      {/* Conversations Sidebar */}
      <div className="w-64 bg-gray-800 overflow-y-auto">
        <div className="p-4 font-bold text-xl border-b border-gray-700">Chats</div>
        {conversations.map((conv) => (
          <div
            key={conv.conversationId}
            className={`flex items-center p-3 cursor-pointer hover:bg-gray-700 ${
              selectedConversation?.conversationId === conv.conversationId ? 'bg-gray-700' : ''
            }`}
            onClick={() => setSelectedConversation(conv)}
          >
            <FiUser className="mr-3 text-2xl" />
            <div className="flex-1">
              <div className="font-semibold">{conv.user.name}</div>
              <div className="text-sm text-gray-400 truncate">{conv.lastMessage?.content}</div>
            </div>
            <FiCircle
              className={`ml-2 ${
                conv.user.isOnline ? 'text-green-500' : 'text-gray-500'
              }`}
            />
          </div>
        ))}
      </div>

      {/* Chat Area */}
      <div className="flex-1 flex flex-col">
        {selectedConversation ? (
          <>
            {/* Chat Header */}
            <div className="bg-gray-800 p-4 flex items-center">
              <FiUser className="mr-3 text-2xl" />
              <div className="flex-1">
                <div className="font-semibold">{selectedConversation.user.name}</div>
                <div className="text-sm text-gray-400">
                  {selectedConversation.user.isOnline ? 'Online' : 'Offline'}
                </div>
              </div>
            </div>

            {/* Messages Area */}
            <div className="flex-1 overflow-y-auto p-4 space-y-3">
              {messages.map((msg) => (
                <div
                  key={msg._id}
                  className={`flex ${
                    msg.senderId._id === getCurrentUserId() ? 'justify-end' : 'justify-start'
                  }`}
                >
                  <div
                    className={`max-w-md p-3 rounded-lg ${
                      msg.senderId._id === getCurrentUserId() ? 'bg-blue-600' : 'bg-gray-700'
                    }`}
                  >
                    {msg.content}
                  </div>
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>

            {/* Message Input */}
            <form onSubmit={handleSendMessage} className="bg-gray-800 p-4 flex">
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type a message..."
                className="flex-1 bg-gray-700 p-2 rounded-l-lg"
              />
              <button type="submit" className="bg-blue-600 p-2 rounded-r-lg">
                <FiSend />
              </button>
            </form>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center">
            <FiMessageSquare className="text-6xl text-gray-600" />
            <p className="ml-4 text-gray-400">Select a conversation</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatInterface;
