import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // for redirection after login/signup
import { FiUser, FiLock, FiPhone } from 'react-icons/fi'; // Icons for form fields
import './AuthForm.css'; // Import your CSS for styles

const AuthForm = () => {
  const [isLogin, setIsLogin] = useState(true); // Toggle between login/signup
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState(''); // Used for signup
  const [phoneNumber, setPhoneNumber] = useState(''); // Used for signup
  const [otp, setOtp] = useState(''); // OTP input for phone verification
  const [submitting, setSubmitting] = useState(false); // Button loading state
  const [errorMessage, setErrorMessage] = useState('');
  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'username') setUsername(value);
    if (name === 'password') setPassword(value);
    if (name === 'name') setName(value);
    if (name === 'phoneNumber') setPhoneNumber(value);
    if (name === 'otp') setOtp(value);
  };

  // Handle login form submission
  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setErrorMessage('');

    const loginData = { username, password };

    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(loginData),
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('token', data.token);
        console.log('Login successful');
        navigate('/chat'); // Redirect to chat page or dashboard
      } else {
        setErrorMessage('Invalid username or password');
      }
    } catch (error) {
      console.error('Error logging in:', error);
      setErrorMessage('An error occurred. Please try again later.');
    } finally {
      setSubmitting(false);
    }
  };

  // Handle signup form submission
  const handleSignupSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setErrorMessage('');

    if (!otpVerified) {
      setErrorMessage('Please verify your phone number first');
      setSubmitting(false);
      return;
    }

    const signupData = { username, password, name, phoneNumber };

    try {
      const response = await fetch('http://localhost:5000/api/auth/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(signupData),
      });

      if (response.ok) {
        console.log('Signup successful');
        navigate('/login'); // Redirect to login page after successful signup
      } else {
        setErrorMessage('Signup failed. Please try again.');
      }
    } catch (error) {
      console.error('Error signing up:', error);
      setErrorMessage('An error occurred. Please try again later.');
    } finally {
      setSubmitting(false);
    }
  };

  // Send OTP to phone number for signup
  const sendOtp = async () => {
    if (!phoneNumber) {
      alert('Please enter a phone number');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/auth/send-otp', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ number: phoneNumber }),
      });

      if (response.ok) {
        setOtpSent(true);
        alert('OTP sent successfully');
      } else {
        alert('Failed to send OTP. Please try again.');
      }
    } catch (error) {
      console.error('Error sending OTP:', error);
      alert('An error occurred. Please try again later.');
    }
  };

  // Verify OTP for phone number
  const verifyOtp = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/auth/verify-otp', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ number: phoneNumber, otp }),
      });

      if (response.ok) {
        setOtpVerified(true);
        alert('OTP verified successfully');
      } else {
        alert('Invalid OTP. Please try again.');
      }
    } catch (error) {
      console.error('Error verifying OTP:', error);
      alert('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="auth-form-container">
      <h2 className="auth-form-title">{isLogin ? 'Login to Chat' : 'Sign Up for Chat'}</h2>
      <form
        onSubmit={isLogin ? handleLoginSubmit : handleSignupSubmit}
        className="auth-form"
      >
        {/* Username */}
        <div className="form-group">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            name="username"
            value={username}
            onChange={handleInputChange}
            required
          />
        </div>

        {/* Password */}
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={handleInputChange}
            required
          />
        </div>

        {/* Fields for Signup */}
        {!isLogin && (
          <>
            <div className="form-group">
              <label htmlFor="name">Full Name</label>
              <input
                type="text"
                id="name"
                name="name"
                value={name}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="phoneNumber">Phone Number</label>
              <input
                type="tel"
                id="phoneNumber"
                name="phoneNumber"
                value={phoneNumber}
                onChange={handleInputChange}
                required
              />
              <button type="button" onClick={sendOtp} disabled={otpSent}>
                {otpSent ? 'OTP Sent' : 'Send OTP'}
              </button>
            </div>

            {otpSent && (
              <div className="form-group">
                <label htmlFor="otp">OTP</label>
                <input
                  type="text"
                  id="otp"
                  name="otp"
                  value={otp}
                  onChange={handleInputChange}
                  required
                />
                <button type="button" onClick={verifyOtp}>
                  Verify OTP
                </button>
              </div>
            )}
          </>
        )}

        {/* Error Message */}
        {errorMessage && <div className="error-message">{errorMessage}</div>}

        {/* Submit Button */}
        <button type="submit" disabled={submitting}>
          {submitting ? 'Processing...' : isLogin ? 'Login' : 'Sign Up'}
        </button>
      </form>

      {/* Toggle between Login and Sign Up */}
      <p>
        {isLogin ? "Don't have an account?" : 'Already have an account?'}
        <button onClick={() => setIsLogin(!isLogin)}>
          {isLogin ? 'Sign Up' : 'Login'}
        </button>
      </p>
    </div>
  );
};

export default AuthForm;
