import React, { useState } from 'react';
import { FiUser, FiLock, FiMail } from 'react-icons/fi';
import { useNavigate } from 'react-router-dom'; // useNavigate instead of useHistory

const AuthForm = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const navigate = useNavigate(); // useNavigate hook for navigation

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setErrorMessage('');

    const url = isLogin ? 'http://localhost:5000/api/auth/login' : 'http://localhost:5000/api/auth/signup';

    const userData = {
      username,
      email,
      password
    };

    // For Login, we don't need username
    if (isLogin) {
      delete userData.username;
    }

    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('token', data.token);
        console.log('Request successful');

        // Redirect based on the email domain
        if (email.endsWith('@ngo.com') || email.endsWith('@ngoAdmin.com')) {
          navigate('/homengo'); // Redirect to the NGO-specific page
        } else {
          navigate('/'); // Redirect to the regular home page
        }
      } else {
        console.error('Request failed');
        setErrorMessage('Invalid email or password. Please try again.');
      }
    } catch (error) {
      console.error('Error occurred:', error);
      setErrorMessage('An error occurred. Please try again later.');
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="bg-[#1E3A8A] p-8 rounded-2xl shadow-2xl w-full max-w-md text-[#E5E7EB] backdrop-blur-lg bg-opacity-50 border-2 border-[#2563EB]">
      <h2 className="text-2xl font-bold mb-6 text-center text-[#FACC15]">Welcome to FuturChat</h2>
      <div className="flex mb-8 bg-[#312E81] rounded-lg overflow-hidden">
        <button
          className={`flex-1 py-3 text-center transition-all duration-300 ${isLogin ? 'bg-[#2563EB] text-[#E5E7EB] shadow-lg' : 'text-[#A78BFA] hover:bg-[#312E81] hover:text-[#E5E7EB]'}`}
          onClick={() => setIsLogin(true)}
        >
          Login
        </button>
        <button
          className={`flex-1 py-3 text-center transition-all duration-300 ${!isLogin ? 'bg-[#2563EB] text-[#E5E7EB] shadow-lg' : 'text-[#A78BFA] hover:bg-[#312E81] hover:text-[#E5E7EB]'}`}
          onClick={() => setIsLogin(false)}
        >
          Sign Up
        </button>
      </div>
      <form onSubmit={handleSubmit} className="space-y-6">
        {!isLogin && (
          <div className="relative">
            <FiUser className="absolute left-3 top-1/2 transform -translate-y-1/2 text-[#A78BFA]" />
            <input
              type="text"
              placeholder="Username"
              className="w-full py-3 pl-10 pr-3 bg-[#312E81] rounded-lg focus:outline-none focus:ring-2 focus:ring-[#2563EB] text-[#E5E7EB] placeholder-[#A78BFA]"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
        )}
        <div className="relative">
          <FiMail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-[#A78BFA]" />
          <input
            type="email"
            placeholder="Email"
            className="w-full py-3 pl-10 pr-3 bg-[#312E81] rounded-lg focus:outline-none focus:ring-2 focus:ring-[#2563EB] text-[#E5E7EB] placeholder-[#A78BFA]"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="relative">
          <FiLock className="absolute left-3 top-1/2 transform -translate-y-1/2 text-[#A78BFA]" />
          <input
            type="password"
            placeholder="Password"
            className="w-full py-3 pl-10 pr-3 bg-[#312E81] rounded-lg focus:outline-none focus:ring-2 focus:ring-[#2563EB] text-[#E5E7EB] placeholder-[#A78BFA]"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {errorMessage && <div className="error-message text-center text-red-500">{errorMessage}</div>}
        <button
          type="submit"
          className="w-full bg-[#2563EB] hover:bg-[#1E40AF] text-[#E5E7EB] font-bold py-3 px-4 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#FACC15] transition-colors duration-300 shadow-lg"
          disabled={submitting}
        >
          {submitting ? 'Logging in...' : isLogin ? 'Login' : 'Sign Up'}
        </button>
      </form>
      {isLogin && (
        <p className="mt-6 text-center text-[#A78BFA] text-sm">
          Don't have an account?{' '}
          <button
            className="text-[#FACC15] hover:text-[#FDE68A] font-semibold transition-colors duration-300"
            onClick={() => setIsLogin(false)}
          >
            Sign up here
          </button>
        </p>
      )}
    </div>
  );
};

export default AuthForm;
